mapper.delete:
	delete tbl_reply
   		where repNum = #{repNum}
       		and userId = #{userId}

mapper.select:
	select userId
		from tbl_reply
   			where repNum = #{repNum}

mapper.update:
update tbl_reply
    set
        repCon = #{repCon}
    where repNum = #{repNum}
        and userId = #{userId}

mapper.delete:
delete tbl_cart
    where cartNum = #{cartNum}
        and userId = #{userId}
